const ASSIGN_ACTION_TYPE = 'machine.actions.assign' as const;

const isProduction = process.env.NODE_ENV === 'production';

type StateChart<State extends string, Context, Event extends { type: string }> = {
  initial: State;
  context: Context;
  on?: {
    [eventType in Event['type']]?: Transition<State, Context, Event>;
  };
  states: {
    [stateValue in State]: {
      on?: {
        [eventType in Event['type']]?: Transition<State, Context, Event>;
      };
      entry?: Array<Action<Context, Event>>;
      exit?: Array<Action<Context, Event>>;
    };
  };
};

type Transition<State extends string, Context, Event> = {
  target?: State;
  actions?: Array<Action<Context, Event>>;
  cond?: (context: Context, Event: Event) => boolean;
};

type Action<Context, Event> = ActionFunction<Context, Event> | AssignAction<Context, Event>;

type ActionFunction<Context, Event> = (
  context: Context,
  event: Event,
  send: (event: Event) => void
) => void;

function createStateMachine<State extends string, Context, Event extends { type: string }>(
  stateChart: StateChart<State, Context, Event>,
  { debug = false, warnOnUnknownTransitions = !isProduction } = {}
) {
  let currentState = stateChart.initial;
  let currentContext = stateChart.context;

  type CallbackFn = (args: { state: State; context: Context }) => void;
  const subscriptions: Array<CallbackFn> = [];

  function subscribe(callback: CallbackFn) {
    subscriptions.push(callback);
    return () => {
      subscriptions.splice(subscriptions.indexOf(callback), 1);
    };
  }

  function notify() {
    subscriptions.forEach((callback) => callback({ state: currentState, context: currentContext }));
  }

  const send = (event: Event) => {
    const stateDefinition = stateChart.states[currentState];
    const type: Event['type'] | undefined = event.type;
    const topLevelTransition: Transition<State, Context, Event> | undefined = stateChart.on?.[type];
    const stateTransition: Transition<State, Context, Event> | undefined =
      stateDefinition.on?.[type];
    const transition = topLevelTransition ?? stateTransition;

    if (transition === undefined) {
      if (warnOnUnknownTransitions) {
        console.warn(
          `From state: "${currentState}", event "${type}" has no transition to any state`
        );
      }
    } else {
      const { target: nextState, actions = [], cond = () => true } = transition;
      const nextStateDefinition = nextState ? stateChart.states[nextState] : {};

      if (cond(currentContext, event)) {
        // execute actions
        const allActions = (stateDefinition.exit || []).concat(
          actions,
          nextStateDefinition.entry || []
        );
        currentContext = executeActions(allActions, event, currentContext);

        if (nextState) {
          currentState = nextState;

          if (debug) {
            console.group('event:', event);
            console.log('state:', currentState);
            console.log('context:', currentContext);
            console.groupEnd();
          }

          notify();
        }
      }
    }
  };

  function getContext() {
    return currentContext;
  }

  function executeActions(
    actions: Array<Action<Context, Event>>,
    event: Event,
    context: Context
  ): Context {
    let nextContext = context;
    actions?.forEach((action) => {
      if (typeof action === 'function') {
        action(nextContext, event, send);
      } else if (action.type === ASSIGN_ACTION_TYPE) {
        nextContext = action.assign(nextContext, event);
      }
    });
    return nextContext;
  }

  return { subscribe, send, getContext };
}

type AssignAction<Context, Event> = {
  type: typeof ASSIGN_ACTION_TYPE;
  assign(context: Context, event: Event): Context;
};

function assign<Context, Event>(
  assignFn: AssignAction<Context, Event>['assign']
): AssignAction<Context, Event> {
  return { type: ASSIGN_ACTION_TYPE, assign: assignFn };
}

export { createStateMachine, assign };
export type { StateChart, Action };
import { assign } from './createStateMachine';

import type { StateChart, Action } from './createStateMachine';

type TooltipState = 'closed' | 'opening' | 'open' | 'closing';

type TooltipEvent =
  | { type: 'OPEN'; id: string; delayDuration?: number }
  | { type: 'CLOSE'; id: string; skipDelayDuration: number }
  | { type: 'FOCUS'; id: string }
  | { type: 'DELAY_TIMER_END' }
  | { type: 'SKIP_DELAY_TIMER_END' };

type TooltipContext = { id: string | null; delayed: boolean };

type TooltipStateChart = StateChart<TooltipState, TooltipContext, TooltipEvent>;
type TooltipAction = Action<TooltipContext, TooltipEvent>;

// actions
let delayTimerId: number;
let skipDelayTimerId: number;

const startDelayTimer: TooltipAction = (context, event, send) => {
  const delayDuration: number | undefined = (event as any).delayDuration;
  const sendTimerEnd = () => send({ type: 'DELAY_TIMER_END' });
  if (delayDuration === undefined) {
    sendTimerEnd();
  } else {
    delayTimerId = window.setTimeout(sendTimerEnd, delayDuration);
  }
};

const cancelDelayTimer: TooltipAction = () => clearTimeout(delayTimerId);

const startSkipDelayTimer: TooltipAction = (context, event, send) => {
  const skipDelayDuration: number = (event as any).skipDelayDuration ?? 300;
  skipDelayTimerId = window.setTimeout(
    () => send({ type: 'SKIP_DELAY_TIMER_END' }),
    skipDelayDuration
  );
};

const cancelSkipDelayTimer: TooltipAction = () => clearTimeout(skipDelayTimerId);

const setId: TooltipAction = assign((context, event) => ({
  ...context,
  id: (event as any).id ?? context.id,
}));
const resetId: TooltipAction = assign((context) => ({ ...context, id: null }));
const setDelayed: TooltipAction = assign((context) => ({ ...context, delayed: true }));
const resetDelayed: TooltipAction = assign((context) => ({ ...context, delayed: false }));

const tooltipStateChart: TooltipStateChart = {
  initial: 'closed',
  context: { id: null, delayed: false },
  on: {
    FOCUS: { target: 'open' },
  },
  states: {
    closed: {
      entry: [resetId],
      on: {
        OPEN: { target: 'opening' },
      },
    },

    opening: {
      entry: [startDelayTimer, setId, setDelayed],
      exit: [cancelDelayTimer],
      on: {
        DELAY_TIMER_END: { target: 'open' },
        CLOSE: { target: 'closed' },
      },
    },

    open: {
      entry: [setId],
      exit: [resetDelayed],
      on: {
        OPEN: { target: 'open' },
        CLOSE: {
          target: 'closing',
          cond: (context, event) => context.id === (event as any).id,
        },
      },
    },

    closing: {
      entry: [startSkipDelayTimer],
      exit: [cancelSkipDelayTimer],
      on: {
        OPEN: { target: 'open' },
        SKIP_DELAY_TIMER_END: { target: 'closed' },
      },
    },
  },
};

export { tooltipStateChart };
import * as React from 'react';
import { composeEventHandlers } from '@radix-ui/primitive';
import { useComposedRefs } from '@radix-ui/react-compose-refs';
import { createContextScope } from '@radix-ui/react-context';
import { useControllableState } from '@radix-ui/react-use-controllable-state';
import { useEscapeKeydown } from '@radix-ui/react-use-escape-keydown';
import { useLayoutEffect } from '@radix-ui/react-use-layout-effect';
import { usePrevious } from '@radix-ui/react-use-previous';
import { useRect } from '@radix-ui/react-use-rect';
import { Presence } from '@radix-ui/react-presence';
import { Primitive } from '@radix-ui/react-primitive';
import * as PopperPrimitive from '@radix-ui/react-popper';
import { createPopperScope } from '@radix-ui/react-popper';
import { Portal } from '@radix-ui/react-portal';
import { Slottable } from '@radix-ui/react-slot';
import * as VisuallyHiddenPrimitive from '@radix-ui/react-visually-hidden';
import { useId } from '@radix-ui/react-id';
import { createStateMachine } from './createStateMachine';
import { tooltipStateChart } from './tooltipStateChart';

import type * as Radix from '@radix-ui/react-primitive';
import type { Scope } from '@radix-ui/react-context';

/* -------------------------------------------------------------------------------------------------
 * State machine
 * -----------------------------------------------------------------------------------------------*/

type StateAttribute = 'closed' | 'delayed-open' | 'instant-open';
const stateMachine = createStateMachine(tooltipStateChart);

/* -------------------------------------------------------------------------------------------------
 * Tooltip
 * -----------------------------------------------------------------------------------------------*/

const TOOLTIP_NAME = 'Tooltip';

type ScopedProps<P> = P & { __scopeTooltip?: Scope };
const [createTooltipContext, createTooltipScope] = createContextScope(TOOLTIP_NAME, [
  createPopperScope,
]);
const usePopperScope = createPopperScope();

type TooltipContextValue = {
  contentId: string;
  open: boolean;
  stateAttribute: StateAttribute;
  trigger: TooltipTriggerElement | null;
  onTriggerChange(trigger: TooltipTriggerElement | null): void;
  onFocus(): void;
  onOpen(): void;
  onClose(): void;
};

const [TooltipProvider, useTooltipContext] =
  createTooltipContext<TooltipContextValue>(TOOLTIP_NAME);

interface TooltipProps {
  open?: boolean;
  defaultOpen?: boolean;
  onOpenChange?: (open: boolean) => void;

  /**
   * The duration from when the mouse enters the trigger until the tooltip gets opened.
   * (default: 700)
   */
  delayDuration?: number;

  /**
   * How much time a user has to enter another trigger without incurring a delay again.
   * (default: 300)
   */
  skipDelayDuration?: number;
  children?: React.ReactNode;
}

const Tooltip: React.FC<TooltipProps> = (props: ScopedProps<TooltipProps>) => {
  const {
    __scopeTooltip,
    children,
    open: openProp,
    defaultOpen = false,
    onOpenChange,
    delayDuration = 700,
    skipDelayDuration = 300,
  } = props;
  const popperScope = usePopperScope(__scopeTooltip);
  const [trigger, setTrigger] = React.useState<HTMLButtonElement | null>(null);
  const contentId = useId();
  const [open = false, setOpen] = useControllableState({
    prop: openProp,
    defaultProp: defaultOpen,
    onChange: onOpenChange,
  });
  const [stateAttribute, setStateAttribute] = React.useState<StateAttribute>(
    openProp ? 'instant-open' : 'closed'
  );

  // control open state using state machine subscription
  React.useEffect(() => {
    const unsubscribe = stateMachine.subscribe(({ state, context }) => {
      if (state === 'open' && context.id === contentId) {
        setOpen(true);
      } else {
        setOpen(false);
      }
    });

    return unsubscribe;
  }, [contentId, setOpen]);

  // sync state attribute with using state machine subscription
  React.useEffect(() => {
    const unsubscribe = stateMachine.subscribe(({ state, context }) => {
      if (context.id === contentId) {
        if (state === 'open') {
          setStateAttribute(context.delayed ? 'delayed-open' : 'instant-open');
        } else {
          setStateAttribute('closed');
        }
      } else {
        setStateAttribute('closed');
      }
    });

    return unsubscribe;
  }, [contentId]);

  const handleFocus = React.useCallback(
    () => stateMachine.send({ type: 'FOCUS', id: contentId }),
    [contentId]
  );
  const handleOpen = React.useCallback(
    () => stateMachine.send({ type: 'OPEN', id: contentId, delayDuration }),
    [contentId, delayDuration]
  );
  const handleClose = React.useCallback(
    () => stateMachine.send({ type: 'CLOSE', id: contentId, skipDelayDuration }),
    [skipDelayDuration, contentId]
  );

  // send transition if the component unmounts
  React.useEffect(() => () => handleClose(), [handleClose]);

  // if we're controlling the component
  // put the state machine in the appropriate state
  useLayoutEffect(() => {
    if (openProp === true) {
      stateMachine.send({ type: 'OPEN', id: contentId });
    }
  }, [contentId, openProp]);

  return (
    <PopperPrimitive.Root {...popperScope}>
      <TooltipProvider
        scope={__scopeTooltip}
        contentId={contentId}
        open={open}
        stateAttribute={stateAttribute}
        trigger={trigger}
        onTriggerChange={setTrigger}
        onFocus={handleFocus}
        onOpen={handleOpen}
        onClose={handleClose}
      >
        {children}
      </TooltipProvider>
    </PopperPrimitive.Root>
  );
};

Tooltip.displayName = TOOLTIP_NAME;

/* -------------------------------------------------------------------------------------------------
 * TooltipTrigger
 * -----------------------------------------------------------------------------------------------*/

const TRIGGER_NAME = 'TooltipTrigger';

type TooltipTriggerElement = React.ElementRef<typeof Primitive.button>;
type PrimitiveButtonProps = Radix.ComponentPropsWithoutRef<typeof Primitive.button>;
interface TooltipTriggerProps extends PrimitiveButtonProps {}

const TooltipTrigger = React.forwardRef<TooltipTriggerElement, TooltipTriggerProps>(
  (props: ScopedProps<TooltipTriggerProps>, forwardedRef) => {
    const { __scopeTooltip, ...triggerProps } = props;
    const context = useTooltipContext(TRIGGER_NAME, __scopeTooltip);
    const popperScope = usePopperScope(__scopeTooltip);
    const composedTriggerRef = useComposedRefs(forwardedRef, context.onTriggerChange);
    return (
      <PopperPrimitive.Anchor asChild {...popperScope}>
        <Primitive.button
          type="button"
          aria-describedby={context.open ? context.contentId : undefined}
          data-state={context.stateAttribute}
          {...triggerProps}
          ref={composedTriggerRef}
          onMouseEnter={composeEventHandlers(props.onMouseEnter, context.onOpen)}
          onMouseLeave={composeEventHandlers(props.onMouseLeave, context.onClose)}
          onMouseDown={composeEventHandlers(props.onMouseDown, context.onClose)}
          onFocus={composeEventHandlers(props.onFocus, context.onFocus)}
          onBlur={composeEventHandlers(props.onBlur, context.onClose)}
          onKeyDown={composeEventHandlers(props.onKeyDown, (event) => {
            if (event.key === 'Enter' || event.key === ' ') {
              context.onClose();
            }
          })}
        />
      </PopperPrimitive.Anchor>
    );
  }
);

TooltipTrigger.displayName = TRIGGER_NAME;

/* -------------------------------------------------------------------------------------------------
 * TooltipContent
 * -----------------------------------------------------------------------------------------------*/

const CONTENT_NAME = 'TooltipContent';

type TooltipContentElement = TooltipContentImplElement;
interface TooltipContentProps extends TooltipContentImplProps {
  /**
   * Used to force mounting when more control is needed. Useful when
   * controlling animation with React animation libraries.
   */
  forceMount?: true;
}

const TooltipContent = React.forwardRef<TooltipContentElement, TooltipContentProps>(
  (props: ScopedProps<TooltipContentProps>, forwardedRef) => {
    const { forceMount, ...contentProps } = props;
    const context = useTooltipContext(CONTENT_NAME, props.__scopeTooltip);
    return (
      <Presence present={forceMount || context.open}>
        <TooltipContentImpl ref={forwardedRef} {...contentProps} />
      </Presence>
    );
  }
);

type TooltipContentImplElement = React.ElementRef<typeof PopperPrimitive.Content>;
type PopperContentProps = Radix.ComponentPropsWithoutRef<typeof PopperPrimitive.Content>;
interface TooltipContentImplProps extends PopperContentProps {
  /**
   * A more descriptive label for accessibility purpose
   */
  'aria-label'?: string;

  /**
   * Whether the Tooltip should render in a Portal
   * (default: `true`)
   */
  portalled?: boolean;
}

const TooltipContentImpl = React.forwardRef<TooltipContentImplElement, TooltipContentImplProps>(
  (props: ScopedProps<TooltipContentImplProps>, forwardedRef) => {
    const {
      __scopeTooltip,
      children,
      'aria-label': ariaLabel,
      portalled = true,
      ...contentProps
    } = props;
    const context = useTooltipContext(CONTENT_NAME, __scopeTooltip);
    const popperScope = usePopperScope(__scopeTooltip);
    const PortalWrapper = portalled ? Portal : React.Fragment;

    useEscapeKeydown(() => context.onClose());

    return (
      <PortalWrapper>
        <CheckTriggerMoved __scopeTooltip={__scopeTooltip} />
        <PopperPrimitive.Content
          data-state={context.stateAttribute}
          {...popperScope}
          {...contentProps}
          ref={forwardedRef}
          style={{
            ...contentProps.style,
            // re-namespace exposed content custom property
            ['--radix-tooltip-content-transform-origin' as any]:
              'var(--radix-popper-transform-origin)',
          }}
        >
          <Slottable>{children}</Slottable>
          <VisuallyHiddenPrimitive.Root id={context.contentId} role="tooltip">
            {ariaLabel || children}
          </VisuallyHiddenPrimitive.Root>
        </PopperPrimitive.Content>
      </PortalWrapper>
    );
  }
);

TooltipContent.displayName = CONTENT_NAME;

/* -------------------------------------------------------------------------------------------------
 * TooltipArrow
 * -----------------------------------------------------------------------------------------------*/

const ARROW_NAME = 'TooltipArrow';

type TooltipArrowElement = React.ElementRef<typeof PopperPrimitive.Arrow>;
type PopperArrowProps = Radix.ComponentPropsWithoutRef<typeof PopperPrimitive.Arrow>;
interface TooltipArrowProps extends PopperArrowProps {}

const TooltipArrow = React.forwardRef<TooltipArrowElement, TooltipArrowProps>(
  (props: ScopedProps<TooltipArrowProps>, forwardedRef) => {
    const { __scopeTooltip, ...arrowProps } = props;
    const popperScope = usePopperScope(__scopeTooltip);
    return <PopperPrimitive.Arrow {...popperScope} {...arrowProps} ref={forwardedRef} />;
  }
);

TooltipArrow.displayName = ARROW_NAME;

/* -----------------------------------------------------------------------------------------------*/

function CheckTriggerMoved(props: ScopedProps<{}>) {
  const { __scopeTooltip } = props;
  const context = useTooltipContext('CheckTriggerMoved', __scopeTooltip);

  const triggerRect = useRect(context.trigger);
  const triggerLeft = triggerRect?.left;
  const previousTriggerLeft = usePrevious(triggerLeft);
  const triggerTop = triggerRect?.top;
  const previousTriggerTop = usePrevious(triggerTop);
  const handleClose = context.onClose;

  React.useEffect(() => {
    // checking if the user has scrolledâ€¦
    const hasTriggerMoved =
      (previousTriggerLeft !== undefined && previousTriggerLeft !== triggerLeft) ||
      (previousTriggerTop !== undefined && previousTriggerTop !== triggerTop);

    if (hasTriggerMoved) {
      handleClose();
    }
  }, [handleClose, previousTriggerLeft, previousTriggerTop, triggerLeft, triggerTop]);

  return null;
}

const Root = Tooltip;
const Trigger = TooltipTrigger;
const Content = TooltipContent;
const Arrow = TooltipArrow;

export {
  createTooltipScope,
  //
  Tooltip,
  TooltipTrigger,
  TooltipContent,
  TooltipArrow,
  //
  Root,
  Trigger,
  Content,
  Arrow,
};
export type { TooltipProps, TooltipTriggerProps, TooltipContentProps, TooltipArrowProps };
